import React from 'react';
import { withStyles } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';
import AcceptOrder from './orderAccept'
import { createMuiTheme } from '@material-ui/core/styles';

const muiTheme = createMuiTheme({ palette: {  primary: { 500: '#337ab7' }, secondary: red, }, })

class Notifications extends React.Component {
  state = {
    open: true,
    isAccept : false,
  };

  // componentDidMount() {
  //   this.timer = setInterval(this.services, 5000);
  // }

  services = () => {
    console.log("inside services");
    let url = "searchRide/"+1;
    this.props.callGetServices(url);
  }

  handleClickOpen = () => {
    this.setState({
      open: true,
    });
  };

  handleClose = () => {
    this.setState({ open: false });
  };

  handleAccept = () =>{
    this.setState({isAccept : true});
    this.handleClose()
  };
  render() {
    return (
      <div>
        <Button variant="outlined" color="secondary"  onClick={this.handleClickOpen} className = { "notificationButton" }>
          Latest
        </Button>
        <Button variant="contained" color="primary" onClick={this.handleClickOpen} className = { "notificationButton" }>
          Accepted
        </Button>
        <Button variant="contained" color="primary" onClick={this.handleClickOpen} className = { "notificationButton" }>
          Rejected
        </Button>
        {this.state.isAccept &&
            <AcceptOrder />
        }
      </div>
    );
  }
}

export default Notifications;
