import React from 'react';
import { withStyles, withTheme } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';
import AcceptOrder from './orderAccept'
import { createMuiTheme,MuiThemeProvider } from '@material-ui/core/styles';
import { red } from '@material-ui/core/styles';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemText from '@material-ui/core/ListItemText';
import {Link}from 'react-router-dom'
import Card from '@material-ui/core/Card';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';


const muiTheme = createMuiTheme({
  palette: {
      primary: {
          main: '#ecebeb',
      },
      secondary: {
          main: '#000',
      },
  },
});

const styles = theme => ({
  root: {
    display: 'flex',
    flexWrap: 'wrap',
  },
  margin: {
    margin: theme.spacing.unit,
  },
  withoutLabel: {
    marginTop: theme.spacing.unit * 3,
  },
  textField: {
    flexBasis: 200,
  },
  card: {
    maxWidth: 445,
  },
  container: {
    display: 'flex',
    flexWrap: 'wrap',
  },
  manDateTime: {
    marginLeft: theme.spacing.unit,
    marginRight: theme.spacing.unit,
    width: 200,
  },
  
});
class Notifications extends React.Component {
  state = {
    open: true,
    isAccept : false,
    latestColor : "primary",
    acceptColor : "primary",
    rejectedColor : "primary",
    latestVariant : 'contained',
    acceptVariant : 'contained',
    rejectedVariant : 'contained',
    inboxBookingDetails : [],
    latestCardVisible : false,
    otherCardVisible : false,
    cardVisible : 0, // 0 - Latest 1 - Other
    viewBookingID : 0
  };

  componentDidMount() {
    this.getBookings("I");
    this.handleClickOpen('latest');
  }

  getBookings = (status) => {
    let url = "searchRide/1/" + status;
    this.props.callGetServices(url).then(value =>{
      this.setState({
        inboxBookingDetails : value.data.bookingDetails
      })
      console.log("value is " + value.data.bookingDetails)
    }).catch(error =>{
      console.log(error);
    })
  }

  viewDetails = (viewBookingID) =>{
    console.log("inside View Details" + viewBookingID)
    var latestCardVisible = false;
    var otherCardVisible = false;
    if(this.state.cardVisible === 0){
      latestCardVisible = true;
    }else{
      otherCardVisible = true;
    }

    this.setState({
      latestCardVisible : latestCardVisible,
      otherCardVisible : otherCardVisible,
      viewBookingID : viewBookingID
    })
  }

  handleClickOpen = (value) => {
    console.log("value is ", value);
    var latestVariant = "";
    var acceptVariant = "";
    var rejectedVariant = "";
    var latestColor = "";
    var acceptColor = "";
    var rejectedColor = "";
    var cardVisible = 0
    var status = "I";
    if(value == 'latest'){
      latestVariant = "outlined";
      acceptVariant = "contained";
      rejectedVariant = "contained";
      latestColor = "secondary";
      acceptColor = "primary";
      rejectedColor = "primary";
      cardVisible = 0;
      status = "I";
    }else if(value == 'accept'){
      latestVariant = "contained";
      acceptVariant = "outlined";
      rejectedVariant = "contained";
      latestColor = "primary";
      acceptColor = "secondary";
      rejectedColor = "primary";
      cardVisible = 1;
      status = "A";
    }else if(value == 'reject'){
      latestVariant = "contained";
      acceptVariant = "contained";
      rejectedVariant = "outlined";
      latestColor = "primary";
      acceptColor = "primary";
      rejectedColor = "secondary";
      cardVisible = 1;
      status = "R";
    }

    this.setState({
      isAccept : true,
      latestVariant : latestVariant,
      acceptVariant : acceptVariant,
      rejectedVariant : rejectedVariant,
      latestColor : latestColor,
      acceptColor : acceptColor,
      rejectedColor : rejectedColor,
      cardVisible : cardVisible
    });
    this.getBookings(status);
  };

  handleClose = () => {
    this.setState({ open: false });
  };

  handleAccept = () =>{
    this.setState({isAccept : true});
    this.handleClose()
  };
  render() {
    const { classes} = this.props;
    return (
      <div>
        <MuiThemeProvider theme={muiTheme}>
          <Button variant={this.state.latestVariant} color={this.state.latestColor}  onClick={() => this.handleClickOpen('latest')} className = { "notificationButton" }>
            Latest
          </Button>
          <Button variant={this.state.acceptVariant} color={this.state.acceptColor} onClick={() => this.handleClickOpen('accept')} className = { "notificationButton" }>
            Accepted
          </Button>
          <Button variant={this.state.rejectedVariant} color={this.state.rejectedColor} onClick={() => this.handleClickOpen('reject')} className = { "notificationButton" }>
            Rejected
          </Button>
        </MuiThemeProvider>
        <List>
          {this.state.inboxBookingDetails.length > 0 ? (
            this.state.inboxBookingDetails.map((value,index) =>(
              value.bookingID % 2 === 0 ?(
                <ListItem key={value.bookingID} className = { "notificationListOdd" }  onClick={() => this.viewDetails(value.bookingID)}>
                  <ListItemText 
                    primary={`BookingID : ${value.bookingID} : Pickup:  ${value.pickupLocation} - Drop : ${value.dropLocation}`}
                  />
                </ListItem>
              ) : (
                <ListItem key={value.bookingID} className = { "notificationListEven" } onClick={() => this.viewDetails(value.bookingID)}>
                  <ListItemText
                    primary={`BookingID : ${value.bookingID} : Pickup:  ${value.pickupLocation} - Drop : ${value.dropLocation}`}
                  />
                </ListItem>
              )
            ))
          ):(
            <div>Currently Bookings not available</div>
          )} 
          {this.state.inboxBookingDetails.map((value,index) =>(
            this.state.latestCardVisible &&  this.state.viewBookingID === value.bookingID &&(
              <Card className={classes.card + " largeCard "}>    
                <List>
                  <br/>
                  <ListItemText className={" listItemReceipt "} primary="Pick Up" secondary={value.pickupLocation} />
                  {/* <ListItemText className={" listItemReceipt "} primary="Drop" secondary={values.drop} />
                  <ListItemText className={" listItemReceipt "} primary="When" secondary={values.when} />
                  <ListItemText className={" listItemReceipt "} primary="Item Type" secondary={values.itemType} />
                  {this.props.productTypeOthersVisible &&
                      <ListItemText className={" listItemReceipt "} primary="Other Product" secondary={values.otherItems} />
                  }
                  <ListItemText className={" listItemReceipt "} primary="Tentative Weight" secondary={values.tentativeWeight} />
                  <ListItemText className={" listItemReceipt "} primary="Delivery Date" secondary={values.deliveryDate + " " + values.deliveryTime} />
                  <ListItemText className={" listItemReceipt "} primary="Vehicle Type" secondary={values.vehicleType} />
                  <ListItemText className={" listItemReceipt "} primary="Store Person Name" secondary={values.storePersonName} />
                  <ListItemText className={" listItemReceipt "} primary="Store Person No" secondary={values.storePersonContactNo} />
                  <ListItemText className={" listItemReceipt "} primary="Payment Mode" secondary={values.paymentMode} /> */}
                </List>
                
                <CardActions className={" confirmButtonAction "}>
                    <Button size="small" color="primary" className="button-sm" 
                      onClick = {this.continue}>
                        CONFIRM BOOKING
                    </Button>
                </CardActions>
              </Card>
            )
          ))}
          
        </List>
        {/* {this.state.isAccept &&
            <AcceptOrder />
        } */}
      </div>
    );
  }
}

// export default Notifications;
export default withStyles(styles)(Notifications);
