import React, {Component} from 'react'
import PropTypes from 'prop-types'
import fetchPopularRepos from '../utils/api'
const SelectLanguage =({selectedLanguae,onSelect})=>{
// const SelectLanguage = ({selectedLanguae})=>{
    const langauges = ['ALL','JS','CSS','RUBY','JAVA']
    return(
        <ul className="language">
            {langauges.map((lang) => (
                <li style = {lang === selectedLanguae ? {color : '#fff'}: null}  
                    onClick = {() => onSelect(lang)}
                    key={lang}
                >
                    <a>{lang}</a>
                </li> 
            ))}
        </ul>
    )
}

const ReportGrid = (props)=>{
    const {repos} = props
    return(
        <ul>
            {repos.map(({name,stargazers_count,owner,html_url},index)=>(
                <li></li>
            ))}
        </ul>
    )
}
SelectLanguage.propTypes = {
    selectedLanguae : PropTypes.string.isRequired,
    onSelect : PropTypes.func.isRequired

}
class P0 extends Component{
    state = {
        selectedLanguae : 'ALL',
        repos : null
    }

    componentDidMount(){
        this.updateLanguage(this.state.selectedLanguae)
    }

    updateLanguage = (lang)=>{
        this.setState(()=>{
            return{
                selectedLanguae : lang,
                repos : null
            }
        });
        fetchPopularRepos(lang)
        .then((repos) =>{
            this.setState(()=>{
                return{
                    repos:repos
                }
            });
        })
    }
    render(){        
        return(
            <div>
                {<SelectLanguage 
                    selectedLanguae = {this.state.selectedLanguae}
                    onSelect = {this.updateLanguage}
                />}
                {<ReportGrid repos ={this.state.repos}/>}
            </div>
        )
        
    }
    
}

export default P0;
