import React, { Component } from 'react';
import redux from 'redux';
import {createStore} from 'redux';

class App extends Component {
  render() {
    return (
      <div className="App">
      </div>
    );
  }
}

export default App;
//create store
// const hello = () => ({welcome : "Hello"})
// const store = createStore(hello);
// console.log(store.getState());

const defaultState = {
  menuName : "View Products",
  page : "products.html",
  menuID : "1235",
  pageContent : "hi"
}

//reducer function
const greetings = (state=defaultState,action) => {
  const {type,pageName} = action
  // switch(action.type){
    switch(type){
    case 'PAGE_LOAD' :
    return {...state, pageContent : `Welcome to  ${pageName}`}
    case 'PAGE_CHANGE' : 
    return {...state,pageContent : "Page has Changed"}
    default :
    return state 
  }
}

const store = createStore(greetings)
console.log(store.getState())
const pageName = "index.html";
store.dispatch({
  type : "PAGE_LOAD",
  pageName
})

console.log(store.getState())
