import React from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import Stepper from '@material-ui/core/Stepper';
import Step from '@material-ui/core/Step';
import StepLabel from '@material-ui/core/StepLabel';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';

import RideDetails from './RideDetails';
import ProductDetails from './ProductDetails'
import BookingConfirmation from './BookingConfirmation'
import BookingSuccess from './BookingSuccess'

const styles = theme => ({
  root: {
    width: '90%',
  },
  button: {
    marginRight: theme.spacing.unit,
  },
  instructions: {
    marginTop: theme.spacing.unit,
    marginBottom: theme.spacing.unit,
  },
});

function getSteps() {
  return ['Select campaign settings', 'Create an ad group', 'Create an ad'];
}

// function getStepContent(step) {
//   switch (step) {
//     case 0:
//       return 'Select campaign settings...';
//     case 1:
//       return 'What is an ad group anyways?';
//     case 2:
//       return 'This is the bit I really care about!';
//     default:
//       return 'Unknown step';
//   }
// }

class BookDelivery extends React.Component {
  state = {
    step: 1,
    skipped: new Set(),
  };

  isStepOptional = step => step === 1;

  handleNext = () => {
    const { step } = this.state;
    let { skipped } = this.state;
    if (this.isStepSkipped(step)) {
      skipped = new Set(skipped.values());
      skipped.delete(step);
    }
    this.setState({
      step: step + 1,
      skipped,
    });
  };

  handleBack = () => {
    this.setState(state => ({
      step: state.step - 1,
    }));
  };

  handleSkip = () => {
    const { step } = this.state;
    if (!this.isStepOptional(step)) {
      // You probably want to guard against something like this,
      // it should never occur unless someone's actively trying to break something.
      throw new Error("You can't skip a step that isn't optional.");
    }

    this.setState(state => {
      const skipped = new Set(state.skipped.values());
      skipped.add(step);
      return {
        step: state.step + 1,
        skipped,
      };
    });
  };

  handleReset = () => {
    this.setState({
      step: 0,
    });
  };

  isStepSkipped(step) {
    return this.state.skipped.has(step);
  }

  getStepContent = () =>{
    const {step} = this.state;
    switch(step){
      case 1:
      return ( 
          <RideDetails
          nextStep = {this.nextStep}
          handleChange = {this.handleChange}
          values = {this.state}/>
      );
      case 2:
      return ( 
        <ProductDetails
        nextStep = {this.nextStep}
        handleChange = {this.handleChange}
        values = {this.state}/>
      );
      case 3:
      return ( 
        <BookingConfirmation
        nextStep = {this.nextStep}
        handleChange = {this.handleChange}
        values = {this.state}/>
      );
      case 4:
      return ( 
          <BookingSuccess
          values = {this.state}
          handleChange = {this.handleChange}/>
      );
    }        
  }

  handleChange = (stateObject) => (e) => {
    this.setState ({ [stateObject] : e.target.value})
    if(e.target.value == "OTHERS"){
      this.setState(() =>{
        return{
          productTypeOthersVisible : true
        }
      })
    }else{
      this.setState(() =>{
        return{
          productTypeOthersVisible : false
        }
      })
    }
  }
  render() {
    const { classes } = this.props;
    const steps = getSteps();
    const { step } = this.state;

    return (
      <div className={classes.root}>
        <br/>
        <br/>
        <br/>
        <br/>
        
        <Stepper step={step}>
          {steps.map((label, index) => {
            const props = {};
            const labelProps = {};
            if (this.isStepOptional(index)) {
              labelProps.optional = <Typography variant="caption">Optional</Typography>;
            }
            if (this.isStepSkipped(index)) {
              props.completed = false;
            }
            return (
              <Step key={label} {...props}>
                <StepLabel {...labelProps}>{label}</StepLabel>
              </Step>
            );
          })}
        </Stepper>
        <div>
          {step === steps.length ? (
            <div>
              <Typography className={classes.instructions}>
                All steps completed - you&apos;re finished
              </Typography>
              <Button onClick={this.handleReset} className={classes.button}>
                Reset
              </Button>
            </div>
          ) : (
            <div>
              <Typography className={classes.instructions}>{this.getStepContent()}</Typography>
              <div>
                <Button
                  disabled={step === 0}
                  onClick={this.handleBack}
                  className={classes.button}
                >
                  Back
                </Button>
                {this.isStepOptional(step) && (
                  <Button
                    variant="contained"
                    color="primary"
                    onClick={this.handleSkip}
                    className={classes.button}
                  >
                    Skip
                  </Button>
                )}
                <Button
                  variant="contained"
                  color="primary"
                  onClick={this.handleNext}
                  className={classes.button}
                >
                  {step === steps.length - 1 ? 'Finish' : 'Next'}
                </Button>
              </div>
            </div>
          )}
        </div>
      </div>
    );
  }
}

BookDelivery.propTypes = {
  classes: PropTypes.object,
};

export default withStyles(styles)(BookDelivery);
