import React from 'react';
import classNames from 'classnames';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import InputAdornment from '@material-ui/core/InputAdornment';
import TextField from '@material-ui/core/TextField';
import Card from '@material-ui/core/Card';
import CardActionArea from '@material-ui/core/CardActionArea';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import Button from '@material-ui/core/Button';
import MenuItem from '@material-ui/core/MenuItem';
import Typography from '@material-ui/core/Typography';
import {connect} from 'react-redux'
import {bindActionCreators} from 'redux'

import Grid from '@material-ui/core/Grid';
import DateFnsUtils from '@date-io/date-fns';
import { MuiPickersUtilsProvider, TimePicker, DatePicker } from 'material-ui-pickers';

const styles = theme => ({
  root: {
    display: 'flex',
    flexWrap: 'wrap',
  },
  margin: {
    margin: theme.spacing.unit,
  },
  withoutLabel: {
    marginTop: theme.spacing.unit * 3,
  },
  textField: {
    flexBasis: 200,
  },
  card: {
    maxWidth: 445,
  },
  container: {
    display: 'flex',
    flexWrap: 'wrap',
  },
  manDateTime: {
    marginLeft: theme.spacing.unit,
    marginRight: theme.spacing.unit,
    width: 200,
  },
  
});

const productTypeDD = [
  {
    value: 'HOUSESHIFTING',
    label: 'HOUSE SHIFTING',
  },
  {
    value: 'OFFICESHIFTING',
    label: 'OFFICE SHIFTING',
  },
  {
    value: 'FRUITSVEG',
    label: 'FRUITS/VEGETABLES BAGGAGE',
  },
  {
    value: 'ELECTRONICITEMS',
    label: 'ELECTRONIC ITEMS',
  },
  {
    value: 'IRONITEMS',
    label: 'IRON ITEMS',
  },
  {
    value: 'CLOTHES',
    label: 'CLOTHES',
  },
  {
    value: 'OTHERS',
    label: 'OTHERS',
  },
];

const transportModeDD = [
  {
    value: 'HOUSESHIFTING',
    label: 'HOUSE SHIFTING',
  },
  {
    value: 'OFFICESHIFTING',
    label: 'OFFICE SHIFTING',
  },
  {
    value: 'FRUITSVEG',
    label: 'FRUITS/VEGETABLES BAGGAGE',
  },
  {
    value: 'ELECTRONICITEMS',
    label: 'ELECTRONIC ITEMS',
  },
  {
    value: 'IRONITEMS',
    label: 'IRON ITEMS',
  },
  {
    value: 'CLOTHES',
    label: 'CLOTHES',
  },
  {
    value: 'OTHERS',
    label: 'OTHERS',
  },
];

class ProductDetails extends React.Component {
  continue = e => {
    e.preventDefault();
    this.props.nextStep();
  }

  render() {
    const { selectedDate,classes,handleChange,values} = this.props;
    return (
        
      <div className = {classes.root + " boodDeliveryMainDiv "}>
      <Card className={classes.card + " card "}>
        <TextField
          select
          className={classNames(classes.margin, classes.textField) + " fields "}
          value={values.productType}
          InputProps={{
              startAdornment: <InputAdornment position="start">PRODUCT TYPE</InputAdornment>,
          }}
          onChange = {handleChange('productType')}
          >
          
          {productTypeDD.map(option => (
              <MenuItem key={option.value} value={option.value}>
              {option.label}
              </MenuItem>
          ))}
        </TextField>
        <br />
        {values.productTypeOthersVisible &&
            <TextField
            className={classNames(classes.margin, classes.textField) + " fields "}
            InputProps={{
                startAdornment: <InputAdornment position="start">OTHERS</InputAdornment>,
            }}
            onChange = {handleChange('otherProducts')}
            />
        }
        {values.productTypeOthersVisible &&
          <br/>
        }
        <TextField
        className={classNames(classes.margin, classes.textField) + " fields "}
        InputProps={{
            startAdornment: <InputAdornment position="start">TENTATIVE WEIGHT</InputAdornment>,
            endAdornment : <InputAdornment position="end">IN KG'S</InputAdornment>,
        }}
        onChange = {handleChange('tentativeWeight')}
        />
        {/* <TextField
          select
          className={classNames(classes.margin, classes.textField) + " fields "}
          value={values.productType}
          InputProps={{
              startAdornment: <InputAdornment position="start">MODE OF TRANSPORT</InputAdornment>,
          }}
          onChange = {handleChange('productType')}
          >
          
          {transportModeDD.map(option => (
              <MenuItem key={option.value} value={option.value}>
              {option.label}
              </MenuItem>
          ))}
        </TextField> */}
         <MuiPickersUtilsProvider utils={DateFnsUtils}>
        <Grid container className={classes.grid} justify="space-around">
          <DatePicker
            margin="normal"
            label="Date picker"
            value={selectedDate}
            onChange={this.handleDateChange}
          />
          <TimePicker
            margin="normal"
            label="Time picker"
            value={selectedDate}
            onChange={this.handleDateChange}
          />
        </Grid>
      </MuiPickersUtilsProvider>
      <br/>
        <CardActions>
            <Button size="small" color="primary" className="button-lg" 
              onClick = {this.continue}>
                CONTINUE
            </Button>
        </CardActions>
      </Card>
      </div>
    );
  }
}

ProductDetails.propTypes = {
  classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(ProductDetails)
