import React, {Component} from 'react'

// const SelectLanguage =({selectedLanguae,onSelect})=>{
const SelectLanguage = (props)=>{
    const langauges = ['ALL','JS','CSS','RUBY','JAVA']
    return(
        <ul className="language">
            {langauges.map((lang) => {
                <li style = {lang === props.selectedLanguae ? {color : '#d0021b'}: null} key={lang}>
                    {lang}
                </li> 
            })}
        </ul>
    )
}
class P0 extends Component{
    state = {
        selectedLanguae : 'ALL'
    }
    render(){
        
        return(
            <div>
                {<SelectLanguage selectedLanguae = {this.state.selectedLanguae}/>}
                {/* <SelectLanguage selectedLanguae = {selectedLanguae},onSelect = {this.} /> */}
            </div>
        )
        
    }
    
}

export default P0;
