import React from 'react';
import { withStyles, withTheme } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';
import AcceptOrder from './orderAccept'
import { createMuiTheme,MuiThemeProvider } from '@material-ui/core/styles';
import { red } from '@material-ui/core/styles';

const muiTheme = createMuiTheme({
  palette: {
      primary: {
          main: '#ecebeb',
      },
      secondary: {
          main: '#000',
      },
  },
});

class Notifications extends React.Component {
  state = {
    open: true,
    isAccept : false,
    color : "primary",
    variant : 'contained'
  };

  // componentDidMount() {
  //   this.timer = setInterval(this.services, 5000);
  // }

  // handleClick(e){ 
  //   this.setState({ color: 'secondary' }) 
  // }

  services = () => {
    console.log("inside services");
    let url = "searchRide/"+1;
    this.props.callGetServices(url);
  }

  handleClickOpen = () => {
    this.setState({
      open: true,
      color: 'secondary',
      variant: 'outlined'
    });
  };

  handleClose = () => {
    this.setState({ open: false });
  };

  handleAccept = () =>{
    this.setState({isAccept : true});
    this.handleClose()
  };
  render() {
    return (
      <div>
        <MuiThemeProvider theme={muiTheme}>
          <Button variant={this.state.variant} color={this.state.color1}  onClick={this.handleClickOpen} className = { "notificationButton" }>
            Latest
          </Button>
          <Button variant={this.state.variant} color={this.state.color2} onClick={this.handleClickOpen} className = { "notificationButton" }>
            Accepted
          </Button>
          <Button variant={this.state.variant} color={this.state.color3} onClick={this.handleClickOpen} className = { "notificationButton" }>
            Rejected
          </Button>
        </MuiThemeProvider>
        
        {this.state.isAccept &&
            <AcceptOrder />
        }
      </div>
    );
  }
}

// export default Notifications;
export default withTheme(muiTheme)(Notifications);
