import React from 'react';
import { withStyles, withTheme } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';
import AcceptOrder from './orderAccept'
import { createMuiTheme } from '@material-ui/core/styles';
import { red } from '@material-ui/core/styles';

const muiTheme = createMuiTheme({ 
  MuiButton: {  
    outlinedPrimary: { 500: '#000' }, 
    secondary: red, 
  }, 
})

class Notifications extends React.Component {
  state = {
    open: true,
    isAccept : false,
    color : "primary"
  };

  // componentDidMount() {
  //   this.timer = setInterval(this.services, 5000);
  // }

  services = () => {
    console.log("inside services");
    let url = "searchRide/"+1;
    this.props.callGetServices(url);
  }

  handleClickOpen = () => {
    this.setState({
      open: true,
    });
  };

  handleClose = () => {
    this.setState({ open: false });
  };

  handleAccept = () =>{
    this.setState({isAccept : true});
    this.handleClose()
  };
  render() {
    return (
      <div>
        <MuiThemeProvider theme={muiTheme}>
          <Button variant="outlined" color="secondary"  onClick={this.handleClickOpen} className = { "notificationButton" }>
            Latest
          </Button>
          <Button variant="contained" color={this.state.color} onClick={this.handleClickOpen} className = { "notificationButton" }>
            Accepted
          </Button>
          <Button variant="outlined" color="primary" onClick={this.handleClickOpen} className = { "notificationButton" }>
            Rejected
          </Button>
        </MuiThemeProvider>
        
        {this.state.isAccept &&
            <AcceptOrder />
        }
      </div>
    );
  }
}

// export default Notifications;
export default withTheme(muiTheme)(Notifications);
