import React, {Component} from 'react';
import PropTypes from 'prop-types';

// import { createStore, applyMiddleware } from 'redux';
// import {Provider} from 'react-redux';
// import { composeWithDevTools } from 'redux-devtools-extension';
// import { reducer as formReducer } from 'redux-form'
// import { createStore, combineReducers } from 'redux'
//materia ui
import { withStyles } from '@material-ui/core/styles';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import Typography from '@material-ui/core/Typography';
import IconButton from '@material-ui/core/IconButton';
import MenuIcon from '@material-ui/icons/Menu';
import MenuItem from '@material-ui/core/MenuItem';
import Menu from '@material-ui/core/Menu';
import {withRouter} from 'react-router-dom'
//browser router
import {BrowserRouter as Router, Route,Switch, Redirect, Link}from 'react-router-dom'
import { save, load } from "redux-localstorage-simple"

//components
import BookDelivery from './_components/bookingDelivery/bookDelivery'
import ViewAccount from './_components/profile/viewAccount'
import BookingHistory from './_components/bookingHistory/bookingHistory'
import Dashboard from './_components/landing/Dashboard'
import BookinServices from './BookingServices'
//reducer
import rootReducer from './rootReducer'
//middleware
import logger from 'redux-logger'
import thunk from 'redux-thunk'
const middleware = [logger,thunk]

// const store = createStore(
//    load(),
  
// composeWithDevTools(applyMiddleware(...middleware,save()))
//   )

  // const rootReducer = combineReducers({
  //   // ...your other reducers here
  //   // you have to pass formReducer under 'form' key,
  //   // for custom keys look up the docs for 'getFormState'
  //   form: formReducer
  // })

  // const store = createStore(rootReducer,{},window.__REDUX_DEVTOOLS_EXTENSION__ &&
  //   window.__REDUX_DEVTOOLS_EXTENSION__()
    
  //   )

const styles = {
  root: {
    flexGrow: 1,
  },
  grow: {
    flexGrow: 1,
  },
  menuButton: {
    marginLeft: -12,
    marginRight: 20,
  },
};

class App extends Component {
  state = {
    auth: true,
    anchorEl: null,
    isAdmin: false
  };
  handleSubmit = values => {
    window.alert(JSON.stringify(values, null, 4));
  };

  handleChange = (stateObject) => (e) => {
    this.setState ({ [stateObject] : e.target.value})
  }

  handleMenu = event => {
    this.setState({ anchorEl: event.currentTarget });
  };

  handleClose = () => {
    this.setState({ anchorEl: null });
  };
  render() {
    const { classes } = this.props;
    const { auth, anchorEl } = this.state;
    const open = Boolean(anchorEl);
     console.log(this.props.location.pathname)
     if(this.props.location.pathname === '/admin'){
       this.setState(()=>{
         return{
          isAdmin : !this.state.isAdmin
         }
       })
     }
    return ( 
      <div className={classes.root}>
      {/* <Provider store={store}> */}
          {/* <AppBar position="static">
            <Toolbar>
              <IconButton 
                className={classes.menuButton} 
                color="inherit" 
                aria-label="Menu"
                aria-owns={open ? 'menu-appbar' : undefined}
                aria-haspopup="true"
                onClick={this.handleMenu}>
                <MenuIcon />
              </IconButton>
              <Menu
                id="menu-appbar"
                anchorEl={anchorEl}
                anchorOrigin={{
                  vertical: 'top',
                  horizontal: 'right',
                }}
                transformOrigin={{
                  vertical: 'top',
                  horizontal: 'right',
                }}
                open={open}
                onClose={this.handleClose}
              >
                <MenuItem component={Link} to='/book' onClick={this.handleClose}>Book Delivery</MenuItem>
                <MenuItem component={Link} to='/history' onClick={this.handleClose}>Booking History</MenuItem>
                <MenuItem component={Link} to='/viewAccount' onClick={this.handleClose}>My account</MenuItem>
              </Menu>
              <Typography variant="h6" color="inherit" className={classes.grow}>
                Ease On Delivery
              </Typography>
            </Toolbar>
          </AppBar> */}
          <BookinServices/>
          
            <Switch>
              {/* <Route exact path='/' render= {props=><BookinServices handleChange={this.handleChange} state={this.state}/>}/> */}


               <Route exact path='/admin' component={Dashboard}/>
              {/* <Route exact path='/' render= {props=><BookDelivery handleChange={this.handleChange} state={this.state}/>}/> */}
              {/* <Route path="/book" render= {props=><BookDelivery handleChange={this.handleChange} state={this.state}/>}/>
              <Route path='/history' authed={this.state.authed} handleSubmit={this.handleSubmit} component={BookingHistory}/>
              <Route path='/viewAccount' authed={this.state.authed} component={ViewAccount}/> */}
              {/* <Route path="/book" render= {props=><BookDelivery handleChange={this.handleChange} state={this.state}/>}/>
              <Route path='/history' authed={this.state.authed} handleSubmit={this.handleSubmit} component={BookingHistory}/>
              <Route path='/viewAccount' authed={this.state.authed} component={ViewAccount}/> */}
            </Switch>
        {/* </Provider> */}
      </div>
    );
  }
}

App.propTypes = {
  classes: PropTypes.object.isRequired,
};

export default withRouter(withStyles(styles)(App));
