import React from 'react';
import classNames from 'classnames';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import InputAdornment from '@material-ui/core/InputAdornment';
import TextField from '@material-ui/core/TextField';
import Card from '@material-ui/core/Card';
import CardActionArea from '@material-ui/core/CardActionArea';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import Button from '@material-ui/core/Button';
import MenuItem from '@material-ui/core/MenuItem';
import Typography from '@material-ui/core/Typography';
import {connect} from 'react-redux'
import {bindActionCreators} from 'redux'
import ProductDetails from './ProductDetails';

const styles = theme => ({
  root: {
    display: 'flex',
    flexWrap: 'wrap',
  },
  margin: {
    margin: theme.spacing.unit,
  },
  withoutLabel: {
    marginTop: theme.spacing.unit * 3,
  },
  textField: {
    flexBasis: 200,
  },
  card: {
    maxWidth: 445,
  },
  
});

const whenDD = [
  {
    value: 'NOW',
    label: 'NOW',
  },
  {
    value: 'LATER',
    label: 'DELIVER LATER',
  },
];


class RideDetails extends React.Component {
  continue = e => {
    e.preventDefault();
    this.props.nextStep();
    console.log("step " + this.state.step);
    if(this.props.step == 2){
      this.setState(() =>{
        return{
          productComponentVisible : true
        }
      })
    }
  }

  render() {
    const { classes,handleChange,values} = this.props;
    return (
        
      <div className = {classes.root + " boodDeliveryMainDiv "}>
      <Card className={classes.card + " card "}>
        <CardContent >
            <Typography gutterBottom variant="h5" component="h2" className={"typography"}>
                Make Delivery Easy
            </Typography>
            <Typography component="p">
                Assign delivery to us sit back and relax.
            </Typography>
        </CardContent>
            <TextField
            className={classNames(classes.margin, classes.textField) + " fields "}
            InputProps={{
                startAdornment: <InputAdornment position="start">PICKUP</InputAdornment>,
            }}
            onChange = {handleChange('pickup')}
            />
            <br/>
            <TextField
            id="drop-location"
            className={classNames(classes.margin, classes.textField) + " fields "}
            InputProps={{
                startAdornment: <InputAdornment position="start">DROP</InputAdornment>,
            }}
            onChange = {handleChange('drop')}
            />
            <br/>
            <TextField
            select
            className={classNames(classes.margin, classes.textField) + " fields "}
            value={values.when}
            InputProps={{
                startAdornment: <InputAdornment position="start">WHEN</InputAdornment>,
            }}
            onChange = {handleChange('when')}
            >
            {whenDD.map(option => (
                <MenuItem key={option.value} value={option.value}>
                {option.label}
                </MenuItem>
            ))}
            </TextField>
        {!this.props.productComponentVisible &&
          <CardActions>
          <Button size="small" color="primary" className="button-lg" 
            onClick = {this.continue}>
              FIND NOW
          </Button>
          </CardActions>
        }    
        {this.props.productComponentVisible &&
          <ProductDetails 
          nextStep = {this.props.nextStep}
          handleChange = {this.props.handleChange}
          values = {this.props}/>
        }
      </Card>
      </div>
    );
  }
}

RideDetails.propTypes = {
  classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(RideDetails)
