import React, { Component } from 'react';
import RideDetails from './RideDetails';
import ProductDetails from './ProductDetails'


class BookDelivery extends Component {
    state = { 
        step : 1,
        pickup : "",
        drop : "",
        when : "",
        productType :"",
        productTypeOthersVisible : false,
        otherProducts: "",
        tentativeWeight: "",
        deliveryDate: "",
        deliveryTime: "",
        deliveryPerson: "",
        deliveryPersonNO: ""
     }

     nextStep = () =>{
         const {step} =this.state;
         this.setState({
            step : step +1
         });
     };
    //  previousStep = () =>{
    //     const {step} =this.state;
    //     this.setState({
    //        step : step -1
    //     });
    // };
    handleChange = (stateObject) => (e) => {
      this.setState ({ [stateObject] : e.target.value})
      console.log(e.target.value);
      if(e.target.value == "OTHERS"){
        this.setState(() =>{
          return{
            productTypeOthersVisible : true
          }
        })
      }else{
        this.setState(() =>{
          return{
            productTypeOthersVisible : false
          }
        })
      }
    }
  
    render() { 
        const {step} = this.state;
        switch(step){
            case 1:
            return ( 
                <ProductDetails 
                nextStep = {this.nextStep}
                handleChange = {this.handleChange}
                values = {this.state}/>
            );
            // case 2:
            // return ( 
            //     <FormPersonalDetails 
            //     nextStep = {this.nextStep}
            //     previousStep = {this.previousStep}
            //     handleChange = {this.handleChange}
            //     values = {values}/>
            // );
            // case 3:
            // return ( 
            //     <FormConfirmDetails 
            //     nextStep = {this.nextStep}
            //     previousStep = {this.previousStep}
            //     handleChange = {this.handleChange}
            //     values = {values}/>
            // );
            // case 4:
            // return ( 
            //     <SuccessPage 
            //     values = {values}/>
            // );
        }        
    }
}

export default BookDelivery;
